import { Component, OnInit, Input } from '@angular/core';

@Component({
  selector: 'app-movie-list',
  templateUrl: './movie-list.component.html',
  styleUrls: ['./movie-list.component.css']
})
export class MovieListComponent implements OnInit { 

  ngOnInit(): void {
  }

  @Input()
  movies: { title: string; releaseDate: Date; price: number; }[] | undefined;

  remove(index: number){
    this.movies?.splice(index, 1);
  }

}
<app-generic-list [list]="movies">
    <ng-container content>
        <div *ngFor="let item of movies; let index = index" class="movie-container">
            <div>
                <h2>{{index}}} {{item.title | uppercase}}</h2>
            </div>
            <div>
                {{item.releaseDate | date:"dd-MMMM-yyyy" | uppercase}}
            </div>
            <div>
                {{item.price | currency}}
            </div>
            <div>
                <button (click)="remove(index)">Remove</button>
            </div>
        </div>

    </ng-container>
</app-generic-list>
